import React, { useRef } from 'react';
import Input from '../../UI/Input';
import classes from './MealsItemForm.module.css';

const MealsItemForm = ()=>{
    const amountref = useRef();
    console.log(amountref);
    return(
        <form className={classes.form}>
        <Input
        ref = {amountref} 
        label="Amount" input={{
            id:'amount',
            type:'number',
            min:1,
            max:'5',
            step:'1',
            defaultValue:'1'
        }}/>
        <button>+ Add</button>
        </form>
    )
}
export default MealsItemForm
